    /** @type {!Element} */
    this.root = root;

    /** @private {!Element} */
    this.boundary_ = boundary || document.documentElement;

    /** @type {cr.ui.FocusRow.Delegate|undefined} */
    this.delegate = opt_delegate;

    /** @protected {!EventTracker} */
    this.eventTracker = new EventTracker;
